const Product = () => {
  const [expandedCard, setExpandedCard] = useState(null);

  const handleLearnMore = (index) => {
    setExpandedCard(expandedCard === index ? null : index);
  };

  return (
    <div className="container">
      <center><h1 class="display-1">INTRODUCTION OF LANGUAGE</h1></center>
      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-4">
        {cardData.map((card, index) => (
          <div className="col mb-4" key={index}>
            <div className="card h-100">
              <img src={card.imageUrl} className="card-img-top" alt={card.title} />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{card.title}</h5>
                <p className="card-text">
                  {expandedCard === index ? card.text : `${card.text.substring(0, 100)}...`}
                </p>
                <button onClick={() => handleLearnMore(index)} className="btn bg-yellow mt-auto">
                  {expandedCard === index ? 'Show less' : 'Learn more'}
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>

  );
};

export default Product;
